import React, { Component } from "react";
import {
  View,
  ScrollView,
  Text,
  Button,
  StyleSheet,
  Image,
  TextInput,
  TouchableOpacity,
} from "react-native";

import { withFormik } from "formik";
import MaterialCommunityIcons from "react-native-vector-icons/MaterialCommunityIcons";

import RootContainer from "../../components/RootContainer";
import BodyContainer from "../../components/BodyContainer";
import Colors from "../../constants/colors";

class NewClient extends Component {
  state = {
    client: {},
  };

  headerButton = (
    <TouchableOpacity onPress={this.props.handleSubmit}>
      <View style={styles.headerButtonView}>
        <Text style={styles.headerButton}>Salvar</Text>
      </View>
    </TouchableOpacity>
  );

  render() {
    let address = [];

    this.props.navigation.setOptions({
      headerTitle: "",
      headerShown: true,
      headerStyle: {
        backgroundColor: Colors.background,
        elevation: 0,
      },
      headerTintColor: Colors.secondaryLight,
      headerRight: () => this.headerButton,
    });

    if (this.props.route.params) {
      this.state.client = this.props.route.params.item;

      address = this.state.client.address.split(/\|/);
    }

    return (
      <RootContainer>
        <BodyContainer>
          <ScrollView style={styles.container}>
            <View style={styles.typeContainer}>
              <View style={styles.addPhotoImgContainer}>
                <TouchableOpacity onPress={() => {}}>
                  <Image
                    style={styles.addPhotoImg}
                    source={require("../../../../assets/add_photo.png")}
                  />
                </TouchableOpacity>
              </View>
            </View>

            <View style={styles.typeContainer}>
              <View style={styles.iconTextInputContainer}>
                <MaterialCommunityIcons
                  name="account-outline"
                  color={Colors.formIcon}
                  size={32}
                />
                <View style={styles.textInputContainer}>
                  <Text></Text>
                  <TextInput
                    style={styles.textInput}
                    placeholder="CPF"
                    value={this.props.values.cpf || this.state.client.cpf}
                    onChangeText={(text) =>
                      this.props.setFieldValue("cpf", text)
                    }
                  />
                </View>
              </View>

              <View style={styles.iconTextInputContainer}>
                <MaterialCommunityIcons
                  name="blank"
                  color={Colors.blankIcon}
                  size={32}
                />
                <View style={styles.textInputContainer}>
                  <Text></Text>
                  <TextInput
                    style={styles.textInput}
                    multiline={true}
                    textAlignVertical="top"
                    placeholder="Nome"
                    value={this.props.values.name || this.state.client.name}
                    onChangeText={(text) =>
                      this.props.setFieldValue("name", text)
                    }
                  />
                </View>
              </View>
            </View>

            <View style={styles.typeContainer}>
              <View style={styles.iconTextInputContainer}>
                <MaterialCommunityIcons
                  name="phone-outline"
                  color={Colors.formIcon}
                  size={32}
                />
                <View style={styles.textInputContainer}>
                  <Text></Text>
                  <TextInput
                    style={styles.textInput}
                    multiline={true}
                    textAlignVertical="top"
                    placeholder="Telefone"
                    value={this.props.values.phone || this.state.client.phone}
                    onChangeText={(text) =>
                      this.props.setFieldValue("phone", text)
                    }
                  />
                </View>
              </View>
            </View>

            <View style={styles.typeContainer}>
              <View style={styles.iconTextInputContainer}>
                <MaterialCommunityIcons
                  name="home-city-outline"
                  color={Colors.formIcon}
                  size={32}
                />
                <View style={styles.textInputContainer}>
                  <Text></Text>
                  <TextInput
                    style={styles.textInput}
                    multiline={true}
                    textAlignVertical="top"
                    placeholder="Logradouro"
                    value={this.props.values.street || address[0]}
                    onChangeText={(text) =>
                      this.props.setFieldValue("street", text)
                    }
                  />
                </View>
              </View>
              <View style={styles.iconTextInputContainer}>
                <MaterialCommunityIcons
                  name="blank"
                  color={Colors.blankIcon}
                  size={32}
                />
                <View style={styles.textInputContainer}>
                  <Text></Text>
                  <TextInput
                    style={styles.textInput}
                    multiline={true}
                    textAlignVertical="top"
                    placeholder="Bairro"
                    value={this.props.values.district || address[1]}
                    onChangeText={(text) =>
                      this.props.setFieldValue("district", text)
                    }
                  />
                </View>
              </View>
              <View style={styles.iconTextInputContainer}>
                <MaterialCommunityIcons
                  name="blank"
                  color={Colors.blankIcon}
                  size={32}
                />
                <View style={styles.textInputContainer}>
                  <Text></Text>
                  <TextInput
                    style={styles.textInput}
                    multiline={true}
                    textAlignVertical="top"
                    placeholder="Cidade"
                    value={this.props.values.city || address[2]}
                    onChangeText={(text) =>
                      this.props.setFieldValue("city", text)
                    }
                  />
                </View>
              </View>
            </View>

            {this.props.isSubmitting && <Text>CAREGANDO...</Text>}
          </ScrollView>
        </BodyContainer>
      </RootContainer>
    );
  }
}

const styles = StyleSheet.create({
  headerButtonView: {
    margin: 12,
  },
  headerButton: {
    padding: 6,
    fontSize: 16,
    color: Colors.saveButton,
    borderWidth: 1,
    borderColor: Colors.saveButton,
    borderRadius: 4,
  },
  typeContainer: {
    marginVertical: 12,
  },
  iconTextInputContainer: {
    flexDirection: "row",
    alignItems: "center",
    marginVertical: 3,
    marginHorizontal: 12,
  },
  textInputContainer: {
    marginHorizontal: 12,
    flex: 1,
  },

  addPhotoImgContainer: {
    marginBottom: 12,
    justifyContent: "center",
    alignItems: "center",
  },
  addPhotoImg: {
    width: 90,
    height: 90,
    borderRadius: 50,
    borderWidth: 1,
    borderColor: Colors.secondary,
  },
  textInputContainerWithUnits: {
    flexDirection: "row",
    justifyContent: "center",
    alignItems: "center",
    marginHorizontal: 12,
  },
  textInput: {
    borderWidth: 1,
    borderRadius: 4,
    borderColor: Colors.secondary,
    color: Colors.secondaryLight,
    paddingHorizontal: 12,
    paddingVertical: 12,
    fontSize: 20,
  },
  descriptionTextInput: {},
});

export default withFormik({
  mapPropsToValues: () => ({ email: "", password: "" }),

  handleSubmit: (values) => {
    console.log(values);
  },
})(NewClient);
