import React, { Component } from "react";

import {
  View,
  Text,
  Button,
  StyleSheet,
  Image,
  FlatList,
  TouchableOpacity,
} from "react-native";

import MaterialCommunityIcons from "react-native-vector-icons/MaterialCommunityIcons";

import Modal from "react-native-modal";

import Header from "../../components/Header";
import TopBarGradient from "../../components/TopBarGradient";
import FloatingButton from "../../components/FloatingButton";
import RootContainer from "../../components/RootContainer";
import HeaderContainer from "../../components/HeaderContainer";
import BodyContainer from "../../components/BodyContainer";
import Colors from "../../constants/colors";
import { clients } from "../../../Controller/db";
import getRandomColor from "../../components/colorGenerator";

export default class Clients extends Component {
  state = {
    clients,
  };

  componentDidMount() {}

  renderItem = ({ item }) => (
    <View style={styles.productContainer}>
      <TouchableOpacity
        style={styles.productButton}
        onPress={() => {
          this.props.navigation.navigate("ClientDetails", {
            item,
          });
        }}
      >
        <View style={styles.nameItemTextView}>
          <MaterialCommunityIcons
            name="account-circle-outline"
            color={getRandomColor()}
            size={48}
          />

          <Text style={styles.nameItemText}>{item.name}</Text>
        </View>
      </TouchableOpacity>
    </View>
  );

  render() {
    return (
      <RootContainer>
        <HeaderContainer>
          <TopBarGradient />
          <Header title="Clientes" />
        </HeaderContainer>
        <BodyContainer>
          <FlatList
            contentContainerStyle={styles.list}
            data={this.state.clients}
            keyExtractor={(item) => item._id}
            renderItem={this.renderItem}
          />

          <FloatingButton
            icon={"plus"}
            onPress={() => {
              this.props.navigation.navigate("NewClient");
            }}
          />
        </BodyContainer>
      </RootContainer>
    );
  }
}

const styles = StyleSheet.create({
  loadingImageView: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  heroImageView: {
    flex: 1.5,
    justifyContent: "center",
    alignItems: "center",
  },
  heroImage: {
    width: 128,
    height: 128,
  },
  list: {
    padding: 0,
  },
  nameItemTextView: {
    flex: 1,
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    marginHorizontal: 12,
    paddingHorizontal: 12,
    paddingVertical: 28,
  },
  nameItemText: {
    marginHorizontal: 12,
    color: Colors.secondary,
    fontSize: 18,
  },
});
