import React, { Component } from "react";

import {
  View,
  Text,
  Button,
  StyleSheet,
  Image,
  ScrollView,
  TouchableOpacity,
} from "react-native";

import MaterialCommunityIcons from "react-native-vector-icons/MaterialCommunityIcons";

import FloatingButton from "../../components/FloatingButton";
import RootContainer from "../../components/RootContainer";
import BodyContainer from "../../components/BodyContainer";
import Colors from "../../constants/colors";
import getRandomColor from "../../components/colorGenerator";

export default class ClientDetails extends Component {
  state = {
    client: this.props.route.params.item,
  };

  headerButton = (
    <TouchableOpacity onPress={this.props.handleSubmit}>
      <View style={styles.headerButtonView}>
        <Text style={styles.headerButton}>Excluir</Text>
      </View>
    </TouchableOpacity>
  );

  render() {
    this.props.navigation.setOptions({
      headerTitle: "",
      headerShown: true,
      headerStyle: {
        backgroundColor: Colors.background,
        elevation: 0,
      },
      headerTintColor: Colors.secondaryLight,
      headerRight: () => this.headerButton,
    });

    const address = this.state.client.address.replace(/\|/g, ", ");

    return (
      <RootContainer>
        <BodyContainer>
          <ScrollView style={styles.container}>
            <View style={styles.typeContainer}>
              <View style={styles.photoImgContainer}>
                <Image
                  style={styles.photoImg}
                  source={this.state.client.thumbnail}
                />
              </View>
            </View>

            <View style={styles.typeContainer}>
              <View style={styles.iconTextInputContainer}>
                <MaterialCommunityIcons
                  name="account-outline"
                  color={Colors.formIcon}
                  size={32}
                />
                <View style={styles.textInputContainer}>
                  <Text style={styles.textInput}>{this.state.client.name}</Text>
                </View>
              </View>

              <View style={styles.iconTextInputContainer}>
                <MaterialCommunityIcons
                  name="blank"
                  color={Colors.blankIcon}
                  size={32}
                />
                <View style={styles.textInputContainer}>
                  <Text style={styles.textInput}>{this.state.client.cpf}</Text>
                </View>
              </View>
            </View>

            <View style={styles.typeContainer}>
              <View style={styles.iconTextInputContainer}>
                <MaterialCommunityIcons
                  name="phone-outline"
                  color={Colors.formIcon}
                  size={32}
                />
                <View style={styles.textInputContainer}>
                  <Text style={styles.textInput}>
                    {this.state.client.phone}
                  </Text>
                </View>
              </View>
            </View>

            <View style={styles.typeContainer}>
              <View style={styles.iconTextInputContainer}>
                <MaterialCommunityIcons
                  name="home-city-outline"
                  color={Colors.formIcon}
                  size={32}
                />
                <View style={styles.textInputContainer}>
                  <Text style={styles.textInput}>{address}</Text>
                </View>
              </View>
            </View>
          </ScrollView>
          <FloatingButton
            icon={"pencil-outline"}
            onPress={() => {
              this.props.navigation.navigate("NewClient", {
                item: this.state.client,
              });
            }}
          />
        </BodyContainer>
      </RootContainer>
    );
  }
}

const styles = StyleSheet.create({
  headerButtonView: {
    margin: 12,
  },
  headerButton: {
    padding: 6,
    fontSize: 16,
    color: Colors.deleteButton,
    borderWidth: 1,
    borderColor: Colors.deleteButton,
    borderRadius: 4,
  },
  typeContainer: {
    marginVertical: 12,
  },
  iconTextInputContainer: {
    flexDirection: "row",
    alignItems: "center",
    marginVertical: 3,
    marginHorizontal: 12,
  },
  textInputContainer: {
    marginHorizontal: 12,
    flex: 1,
  },

  photoImgContainer: {
    marginBottom: 12,
    justifyContent: "center",
    alignItems: "center",
  },
  photoImg: {
    width: 140,
    height: 140,
    borderRadius: 70,
    borderWidth: 1,
    borderColor: Colors.secondary,
  },
  textInput: {
    borderBottomWidth: 1,
    borderRadius: 4,
    borderColor: Colors.secondary,
    color: Colors.secondaryLight,
    paddingHorizontal: 12,
    paddingVertical: 12,
    fontSize: 20,
  },
});
