import React, { Component } from "react";
import {
  View,
  ScrollView,
  Text,
  Button,
  StyleSheet,
  Image,
  TextInput,
  TouchableOpacity,
} from "react-native";

import { withFormik } from "formik";
import MaterialCommunityIcons from "react-native-vector-icons/MaterialCommunityIcons";

import FloatingButton from "../../components/FloatingButton";
import RootContainer from "../../components/RootContainer";
import BodyContainer from "../../components/BodyContainer";
import Colors from "../../constants/colors";

class NewProduct extends Component {
  state = {
    product: {},
  };

  headerButton = (
    <TouchableOpacity onPress={this.props.handleSubmit}>
      <View style={styles.headerButtonView}>
        <Text style={styles.headerButton}>Salvar</Text>
      </View>
    </TouchableOpacity>
  );

  render() {
    this.props.navigation.setOptions({
      headerTitle: "",
      headerShown: true,
      headerStyle: {
        backgroundColor: Colors.background,
        elevation: 0,
      },
      headerTintColor: Colors.secondaryLight,
      headerRight: () => this.headerButton,
    });

    if (this.props.route.params) {
      this.state.product = this.props.route.params.item;
    }

    return (
      <RootContainer>
        <BodyContainer>
          <ScrollView style={styles.container}>
            <View style={styles.typeContainer}>
              <View style={styles.addPhotoImgContainer}>
                <TouchableOpacity onPress={() => {}}>
                  <Image
                    style={styles.addPhotoImg}
                    source={
                      require("../../../../assets/add_photo.png") ||
                      this.state.product.thumbnail
                    }
                  />
                </TouchableOpacity>
              </View>
            </View>

            <View style={styles.typeContainer}>
              <View style={styles.iconTextInputContainer}>
                <MaterialCommunityIcons
                  name="cube"
                  color={Colors.formIcon}
                  size={32}
                />
                <View style={styles.textInputContainer}>
                  <Text></Text>
                  <TextInput
                    style={{
                      ...styles.textInput,
                      ...styles.descriptionTextInput,
                    }}
                    multiline={true}
                    textAlignVertical="top"
                    placeholder="Descrição"
                    value={
                      this.props.values.description ||
                      this.state.product.description
                    }
                    onChangeText={(text) =>
                      this.props.setFieldValue("description", text)
                    }
                  />
                </View>
              </View>
            </View>

            <View style={styles.typeContainer}>
              <View style={styles.iconTextInputContainer}>
                <MaterialCommunityIcons
                  name="currency-usd"
                  color={Colors.formIcon}
                  size={32}
                />
                <View style={styles.textInputContainer}>
                  <Text>R$</Text>
                  <TextInput
                    style={styles.textInput}
                    type="number"
                    placeholder="Preço de custo"
                    value={
                      this.props.values.costPrice ||
                      String(this.state.product.costPrice || "")
                    }
                    onChangeText={(text) =>
                      this.props.setFieldValue("costPrice", text)
                    }
                  />
                </View>
              </View>

              <View style={styles.iconTextInputContainer}>
                <MaterialCommunityIcons
                  name="blank"
                  color={Colors.blankIcon}
                  size={32}
                />
                <View style={styles.textInputContainer}>
                  <Text>R$</Text>
                  <TextInput
                    style={styles.textInput}
                    keyboardType="decimal-pad"
                    placeholder="Preço de venda"
                    value={
                      this.props.values.sellPrice ||
                      String(this.state.product.sellPrice || "")
                    }
                    onChangeText={(text) =>
                      this.props.setFieldValue("sellPrice", text)
                    }
                  />
                </View>
              </View>
            </View>

            <View style={styles.typeContainer}>
              <View style={styles.iconTextInputContainer}>
                <MaterialCommunityIcons
                  name="sale"
                  color={Colors.discountIcon}
                  size={32}
                />
                <View style={styles.textInputContainer}>
                  <Text>R$</Text>
                  <TextInput
                    style={styles.textInput}
                    keyboardType="decimal-pad"
                    placeholder="Desconto"
                    value={
                      this.props.values.discount ||
                      String(this.state.product.discount || "")
                    }
                    onChangeText={(text) =>
                      this.props.setFieldValue("discount", text)
                    }
                  />
                </View>
              </View>
            </View>

            <View style={styles.typeContainer}>
              <View style={styles.iconTextInputContainer}>
                <MaterialCommunityIcons
                  name="inbox-multiple-outline"
                  color={Colors.formIcon}
                  size={32}
                />
                <View style={styles.textInputContainer}>
                  <Text>Unidades</Text>
                  <TextInput
                    style={styles.textInput}
                    keyboardType="number-pad"
                    placeholder="Estoque"
                    value={
                      this.props.values.stock ||
                      String(this.state.product.stock || "")
                    }
                    onChangeText={(text) =>
                      this.props.setFieldValue("stock", text)
                    }
                  />
                </View>
              </View>
            </View>
          </ScrollView>
        </BodyContainer>
      </RootContainer>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  headerButtonView: {
    margin: 12,
  },
  headerButton: {
    padding: 6,
    fontSize: 16,
    color: Colors.saveButton,
    borderWidth: 1,
    borderColor: Colors.saveButton,
    borderRadius: 4,
  },
  typeContainer: {
    marginVertical: 12,
  },
  iconTextInputContainer: {
    flexDirection: "row",
    alignItems: "center",
    marginVertical: 3,
    marginHorizontal: 12,
  },
  textInputContainer: {
    marginHorizontal: 12,
    flex: 1,
  },

  addPhotoImgContainer: {
    marginBottom: 12,
    justifyContent: "center",
    alignItems: "center",
  },
  addPhotoImg: {
    width: 90,
    height: 90,
    borderRadius: 50,
    borderWidth: 1,
    borderColor: Colors.secondary,
  },
  textInputContainerWithUnits: {
    flexDirection: "row",
    justifyContent: "center",
    alignItems: "center",
    marginHorizontal: 12,
  },
  textInput: {
    borderWidth: 1,
    borderRadius: 4,
    borderColor: Colors.secondary,
    color: Colors.secondaryLight,
    paddingHorizontal: 12,
    paddingVertical: 12,
    fontSize: 20,
  },
  descriptionTextInput: {},
});

export default withFormik({
  mapPropsToValues: () => ({ email: "", password: "" }),

  handleSubmit: (values) => {
    console.log(values);
  },
})(NewProduct);
