import React, { Component } from "react";

import {
  View,
  Text,
  Button,
  StyleSheet,
  Image,
  TouchableOpacity,
  ScrollView,
} from "react-native";

import MaterialCommunityIcons from "react-native-vector-icons/MaterialCommunityIcons";

import FloatingButton from "../../components/FloatingButton";
import RootContainer from "../../components/RootContainer";
import BodyContainer from "../../components/BodyContainer";
import Colors from "../../constants/colors";

export default class ProductDetails extends Component {
  state = {
    product: this.props.route.params.item,
  };

  headerButton = (
    <TouchableOpacity onPress={this.props.handleSubmit}>
      <View style={styles.headerButtonView}>
        <Text style={styles.headerButton}>Excluir</Text>
      </View>
    </TouchableOpacity>
  );

  render() {
    this.props.navigation.setOptions({
      headerTitle: "",
      headerShown: true,
      headerStyle: {
        backgroundColor: Colors.background,
        elevation: 0,
      },
      headerTintColor: Colors.secondaryLight,
      headerRight: () => this.headerButton,
    });

    return (
      <RootContainer>
        <BodyContainer>
          <ScrollView style={styles.container}>
            <View style={styles.typeContainer}>
              <View style={styles.photoImgContainer}>
                <TouchableOpacity onPress={() => {}}>
                  <Image
                    style={styles.photoImg}
                    source={this.state.product.thumbnail}
                  />
                </TouchableOpacity>
              </View>
            </View>

            <View style={styles.typeContainer}>
              <View style={styles.iconTextInputContainer}>
                <MaterialCommunityIcons
                  name="cube"
                  color={Colors.formIcon}
                  size={32}
                />
                <View style={styles.textInputContainer}>
                  <Text style={styles.textInput}>
                    {this.state.product.description}
                  </Text>
                </View>
              </View>
            </View>

            <View style={styles.typeContainer}>
              <View style={styles.iconTextInputContainer}>
                <MaterialCommunityIcons
                  name="currency-usd"
                  color={Colors.formIcon}
                  size={32}
                />
                <View style={styles.textInputContainer}>
                  <Text style={styles.textInput}>
                    R$ {this.state.product.costPrice}
                  </Text>
                </View>
              </View>

              <View style={styles.iconTextInputContainer}>
                <MaterialCommunityIcons
                  name="blank"
                  color={Colors.blankIcon}
                  size={32}
                />
                <View style={styles.textInputContainer}>
                  <Text style={styles.textInput}>
                    R$ {this.state.product.sellPrice}
                  </Text>
                </View>
              </View>
            </View>

            <View style={styles.typeContainer}>
              <View style={styles.iconTextInputContainer}>
                <MaterialCommunityIcons
                  name="sale"
                  color={Colors.discountIcon}
                  size={32}
                />
                <View style={styles.textInputContainer}>
                  <Text style={styles.textInput}>
                    R$ {this.state.product.discount}
                  </Text>
                </View>
              </View>
            </View>

            <View style={styles.typeContainer}>
              <View style={styles.iconTextInputContainer}>
                <MaterialCommunityIcons
                  name="inbox-multiple-outline"
                  color={Colors.formIcon}
                  size={32}
                />
                <View style={styles.textInputContainer}>
                  <Text style={styles.textInput}>
                    {this.state.product.stock}
                  </Text>
                </View>
              </View>
            </View>
          </ScrollView>
          <FloatingButton
            icon={"pencil-outline"}
            onPress={() => {
              this.props.navigation.navigate("NewProduct", {
                item: this.state.product,
              });
            }}
          />
        </BodyContainer>
      </RootContainer>
    );
  }
}

const styles = StyleSheet.create({
  headerButtonView: {
    margin: 12,
  },
  headerButton: {
    padding: 6,
    fontSize: 16,
    color: Colors.deleteButton,
    borderWidth: 1,
    borderColor: Colors.deleteButton,
    borderRadius: 4,
  },
  typeContainer: {
    marginVertical: 12,
  },
  iconTextInputContainer: {
    flexDirection: "row",
    alignItems: "center",
    marginVertical: 3,
    marginHorizontal: 12,
  },
  textInputContainer: {
    marginHorizontal: 12,
    flex: 1,
  },
  photoImgContainer: {
    marginBottom: 12,
    justifyContent: "center",
    alignItems: "center",
  },
  photoImg: {
    width: 140,
    height: 140,
    borderRadius: 70,
    borderWidth: 1,
    borderColor: Colors.secondary,
  },
  textInput: {
    borderBottomWidth: 1,
    borderRadius: 4,
    borderColor: Colors.secondary,
    color: Colors.secondaryLight,
    paddingHorizontal: 12,
    paddingVertical: 12,
    fontSize: 20,
  },
});
