import React, { Component } from "react";
import {
  TouchableOpacity,
  View,
  Text,
  StyleSheet,
  Image,
  Modal,
  TextInput,
  FlatList,
} from "react-native";
import MaterialCommunityIcons from "react-native-vector-icons/MaterialCommunityIcons";
import { Picker } from "@react-native-community/picker";

import ItemForSearch from "./ItemForSearch";

import Colors from "../constants/colors";

export default class SearchPage extends Component {
  state = {
    selectedValue: 0,
    listOptionsPicker: [],
  };

  render() {
    for (let i = 1; i <= 100; i++) {
      this.state.listOptionsPicker.push(
        <Picker.Item label={`${i}`} value={`${i}`} />
      );
    }

    return (
      <Modal
        visible={this.props.visible}
        onRequestClose={this.props.onRequestClose}
      >
        <TouchableOpacity
          style={styles.buttonBottom}
          onPress={this.props.cancelButtonOnPress}
        >
          <MaterialCommunityIcons
            name="arrow-left"
            size={32}
            color={Colors.primaryDark}
          />
        </TouchableOpacity>
        <View style={styles.screenScrollViewContainer}>
          <FlatList
            keyboardShouldPersistTaps={"handled"}
            renderItem={({ item }) => (
              <ItemForSearch
                style={{ ...styles.screenScrollView, ...styles.reverseItem }}
                onpress={this.props.onItemSelected.bind(this, {
                  item,
                  selectedValue,
                })}
                imgSrc={require("../../../assets/macbook.jpg")}
                text={
                  item.description +
                  "\nR$" +
                  (item.sellPrice - item.discount).toFixed(2)
                }
              />
            )}
            data={this.props.searchArr}
            keyExtractor={(item) => item._id}
            style={styles.reverseItem}
          />
        </View>
        <View style={styles.inputBottomContainer}>
          <TextInput
            style={styles.inputBottom}
            placeholder="Procure um produto"
            onChangeText={this.props.onChangeText}
          />
          <View style={styles.pickerNumberView}>
            <Picker
              selectedValue={this.state.selectedValue}
              style={{ height: 50, width: 100 }}
              onValueChange={(itemValue, itemIndex) =>
                this.setState({ selectedValue: itemValue })
              }
            >
              {this.state.listOptionsPicker}
            </Picker>
          </View>
        </View>
      </Modal>
    );
  }
}

const styles = StyleSheet.create({
  screenScrollViewContainer: {
    justifyContent: "flex-end",
    flex: 1,
  },
  screenScrollView: {
    fontSize: 14,
    marginHorizontal: 24,
  },
  inputBottomContainer: {
    flexDirection: "row",
  },
  pickerNumberView: {
    justifyContent: "center",
    alignItems: "center",
    paddingTop: 0,
  },
  inputBottom: {
    flex: 1,
    paddingVertical: 10,
    paddingHorizontal: 15,
    margin: 6,
    borderRadius: 25,
    elevation: 5,
    backgroundColor: Colors.background,
    color: Colors.secondary,
    fontSize: 16,
  },
  buttonBottom: {
    alignSelf: "flex-start",
    alignItems: "center",
    justifyContent: "center",
    margin: 12,
    width: 50,
    height: 50,
    borderWidth: 1,
    borderRadius: 25,
    borderColor: Colors.background,
    backgroundColor: Colors.background,
  },
  reverseItem: {
    transform: [{ scaleY: -1 }],
  },
});
