import React, { Component } from "react";

import {
  View,
  Text,
  StyleSheet,
  Image,
  FlatList,
  TextInput,
  TouchableOpacity,
  ScrollView,
  Modal,
} from "react-native";

import Header from "../../components/Header";
import RootContainer from "../../components/RootContainer";
import HeaderContainer from "../../components/HeaderContainer";
import BodyContainer from "../../components/BodyContainer";
import ItemForSearch from "../../components/ItemForSearch";
import FloatingButton from "../../components/FloatingButton";
import SearchPage from "../../components/SearchPage";
import Colors from "../../constants/colors";

import { productTypes } from "../../../Controller/db";

export default class NewSale extends Component {
  state = {
    total: 0,
    products: productTypes,
    searchItens: [],
    search: "",
    sellItens: [],
    isSearchScreenVisible: false,
  };

  headerButton = (
    <TouchableOpacity onPress={this.props.handleSubmit}>
      <View style={styles.headerButtonView}>
        <Text style={styles.headerButton}>Continuar</Text>
      </View>
    </TouchableOpacity>
  );

  renderItem = ({ item }) => (
    <View>
      <ItemForSearch
        onpress={() => {}}
        imgSrc={item.item.thumbnail}
        text={
          item.item.item._id +
          " - " +
          item.item.description +
          "\nR$" +
          Number(item.sub_total).toFixed(2) +
          "\n x" +
          item.item.qtd
        }
      />
    </View>
  );

  onChangeTextHandler = (search) => {
    if (search === "") {
      this.setState({ isSearchScreenVisible: false });
      return;
    }

    this.setState({ isSearchScreenVisible: true });
    this.setState({ search });
    const arr = this.state.products.filter((item) =>
      item.description.toLowerCase().includes(search.toLowerCase())
    );
    this.setState({ searchItens: arr });
  };

  onItemSelectHandler = (item, selectedValue) => {
    this.setState({ isSearchScreenVisible: false });
    const arr = this.state.sellItens;
    let sub_total = (item.sellPrice - item.discount) * selectedValue;

    arr.push({
      _id: Math.random() * (5000 - 0) + 0,
      item: item,
      qtd: electedValue,
      sub_total,
    });

    sub_total += this.state.total;

    this.setState({ sellItens: arr, total: sub_total });
  };

  render() {
    this.props.navigation.setOptions({
      headerTitle: `R$ ${Number(this.state.total).toFixed(2)}`,
      headerShown: true,
      headerStyle: {
        backgroundColor: Colors.primary,
        elevation: 0,
      },
      headerRight: () => this.headerButton,
    });

    return (
      <RootContainer>
        <BodyContainer>
          {this.state.sellItens.length > 0 ? (
            <View></View>
          ) : (
            <View style={styles.headerContainer}>
              <Image
                source={require("../../../../assets/new_sale_hero.png")}
                style={styles.heroImage}
              />
            </View>
          )}
          <FlatList
            contentContainerStyle={styles.list}
            data={this.state.sellItens}
            keyExtractor={(item) => item._id}
            renderItem={this.renderItem}
            onChangeText={this.renderItem}
          />
          <FloatingButton
            icon={"cart-arrow-down"}
            onPress={() => {
              this.setState({ isSearchScreenVisible: true });
            }}
          />

          <SearchPage
            visible={this.state.isSearchScreenVisible}
            onRequestClose={() => {
              this.setState({ isSearchScreenVisible: false });
            }}
            cancelButtonOnPress={() => {
              this.setState({ isSearchScreenVisible: false });
            }}
            searchArr={this.state.searchItens}
            onChangeText={this.onChangeTextHandler}
            onItemSelected={this.onItemSelectHandler}
          />
        </BodyContainer>
      </RootContainer>
    );
  }
}

const styles = StyleSheet.create({
  headerContainer: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  heroImage: {
    width: 128,
    height: 128,
  },
  list: { padding: 0 },
  headerButtonView: {
    margin: 12,
  },
  headerButton: {
    padding: 6,
    fontSize: 16,
    color: Colors.secondary,
    borderWidth: 1,
    borderColor: Colors.secondary,
    borderRadius: 4,
  },
});
